          while (true) {
            try {
              b = reader.read();
            } catch (IOException e) {
              throw new Error(e);
            }
            if (b == -1) {
              executor_ExecIdleBatch.shutdown();
              break;
            }
            else {
              synchronized (sb) {
                sb.append((char) b);
              }
            }
          }
        }

      };
