    private static class WellNamedResultHolder {
        private A aResult;
        private B bResult;
        private C cResult;
        private D dResult;

        // Getters

        public WellNamedResultHolder withAResult(final A aResult) {
            this.aResult = aResult;
            return this;
        }

        public WellNamedResultHolder withBResult(final B bResult) {
            this.bResult = bResult;
