import java.math.BigDecimal;
import java.math.MathContext;
import java.math.RoundingMode;
import java.text.Bidi;
import java.util.concurrent.ExecutorService;
import java.util.concurrent.Executors;
import java.util.concurrent.TimeUnit;

public class PiCalculator {
        public static class PICal implements Runnable
        {
            public static BigDecimal floatingPoint;
            public static boolean flag = true;
            public static MathContext mc;
            private final Long number;
            private final Long iter;
            public static BigDecimal pi;
            public PICal(Long number,Long iter) {
                this.number = number;
                this.iter=iter;
            }
            public synchronized void job(BigDecimal n,boolean flag)
            {
                if(flag)
                    pi = pi.add(n);
                else
                    pi = pi.subtract(n);
            }
            @Override
            public void run() {
                BigDecimal term;
                Long i;
                boolean add = number % 2 != 1;
                for (i = number; i &lt; iter ; i ++) {
                    term = BigDecimal.ONE.divide(BigDecimal.valueOf(2L * i + 1), mc);
                    if (term.abs().compareTo(floatingPoint) &lt; 0) {
                        if(i.equals(number))
                            flag = false;
                        return;
                    }
                    job(term, add);
                    add = !add;
                }
            }
        }
    public static void main(String[] args) {
        int floatingPoint = 8;
        BigDecimal base = new BigDecimal(&quot;0.1&quot;);
        PICal.floatingPoint = base.pow(floatingPoint+1);
        PICal.mc = new MathContext(floatingPoint+1);
        PICal.pi = BigDecimal.ZERO;
        ExecutorService executor = Executors.newCachedThreadPool();
        Long power = (long) Math.pow(10 ,6);
        for (int i = 0; i &lt; 100 &amp;&amp; PICal.flag ; i++) {
            executor.execute(new PICal(i*power,(i+1)*power));
        }
        executor.shutdown();
        try {
            executor.awaitTermination(Long.MAX_VALUE, TimeUnit.NANOSECONDS);
        } catch (InterruptedException e) {
            throw new RuntimeException(e);
