public static List&lt;Integer&gt; getMax(List&lt;Integer&gt; list, int limit) { 
    for (int i = list.size()-limit; i &gt;= 0; i--) {
        List&lt;Integer&gt; candidate = list.subList(i,i+limit);
        if (isValid(candidate)) {
