                        valuesBuilder.append(&#39;)&#39;);
                    } else {
                        valuesBuilder.append(&quot;NULL)&quot;);
                    }
        
        
        
                    if (++insertCount % batchSize == 0) {
                        
                        sqlBuilder.append(sqlStart);
                        sqlBuilder.append(valuesBuilder);
