                stmt = con.prepareStatement(sqlBuilder.toString());
                sqlBuilder = new StringBuilder();
                valuesBuilder = new StringBuilder();
                stmt.executeUpdate();
                
        
            } catch (SQLException ex) {
                System.out.println(ex.getMessage());
                ex.printStackTrace();
            } catch (DataAccessException ex) {
                ex.printStackTrace();
            }
        
            finally {
                try {
