        myList.removeAll();

        this.students = students;
        for (int i = 0; i &lt; this.students.size(); i++) { 
            listRow = new StudentRow(students.get(i), events, i);
            myList.add(listRow);
        }

        myList.revalidate();
        setViewportView(BorderLayoutPanel);
