            Gdx.input.setInputProcessor(new InputProcessor() {
                @Override
                public boolean touchDown(int x, int y, int pointer, int button) {
                    if (button == Input.Buttons.LEFT) {
                        onMouseDown();
                        return true;
                    }
                    return false
                }
                
                ... the other implementations for InputProcessor go here, if you&#39;re using Eclipse or Intellij they&#39;ll add them in automatically ...
            });
        }
        
        private void onMouseDown() {
        }
    }

