    import javax.crypto.spec.SecretKeySpec;


    @Bean
    public JWKSource&lt;SecurityContext&gt; jwkSource() {
        return new ImmutableSecret&lt;&gt;(new SecretKeySpec(settings.getJwt().getSecret().getBytes(StandardCharsets.UTF_8), &quot;MHmacSHA256&quot;));
    }


  [1]: https://medium.com/@swayamraina/symmetric-vs-asymmetric-jwts-bd5d1a9567f6
  [2]: https://www.javadoc.io/doc/com.nimbusds/nimbus-jose-jwt/latest/com/nimbusds/jose/jwk/JWKSet.html#toJSONObject()
  [3]: https://i.stack.imgur.com/QJh2V.png

