	private ArrayList&lt;Map&lt;String, Object&gt;&gt; getMetricMeasurements(String metric) throws Exception {
		Object individualMetric = null;
		Map&lt;String, Object&gt; metricMap = null;
		MBeanServer mBeanServer = ManagementFactory.getPlatformMBeanServer();
		ObjectName metricsName = null;
		MBeanInfo mBeanInfo = null;
		ArrayList&lt;Map&lt;String, Object&gt;&gt; measurementList = null;

		try {
			LOGGER.info(&quot;looking at metricsObject&quot;);
            //this will be either the default spring domain or your defined one
            metricsName = new ObjectName(&quot;org.springframework.boot:type=Endpoint,name=Metrics&quot;);
			LOGGER.info(metricsName);
			LOGGER.info(&quot;looking at mBeanInfo&quot;);
			mBeanInfo = mBeanServer.getMBeanInfo(metricsName);
			LOGGER.info(mBeanInfo);

			if (mBeanInfo != null) {
				try {
					Object[] metricParam = { metric };
					String[] metricSignature = { String.class.getName(), List.class.getName() };
					LOGGER.info(&quot;looking at individualMetric&quot;);
					individualMetric = mBeanServer.invoke(metricsName, &quot;metric&quot;, metricParam, metricSignature);
					LOGGER.info(individualMetric);
				} catch (Exception e) {
					LOGGER.error(e.getMessage());
				}
			}

			if (individualMetric != null) {
				ObjectMapper JSONMapper = new ObjectMapper();
				String jsonString = JSONMapper.writeValueAsString(individualMetric);
				LOGGER.info(&quot;metricMap&quot;);
				metricMap = JSONMapper.readValue(jsonString, Map.class);
				LOGGER.info(metricMap);
				LOGGER.info(&quot;measurementList&quot;);
				measurementList = (ArrayList&lt;Map&lt;String, Object&gt;&gt;) metricMap.get(&quot;measurements&quot;);
				LOGGER.info(measurementList);
			}
		} catch (Exception e) {
			LOGGER.error(e.getMessage());
		}

		return measurementList;
	}


```

