    public final static int DEFENSE_MAX = 8;
    public static int rounds_p = 0;

    /**
     * Converts a combat action to its corresponding text representation.
     * @param action The combat action
     * @return Text representation of the action
     */
    public String actionToText(int action) {
        switch (action) {
            case ATTACK:
                return &quot;ATTACK&quot;;
            case DEFENSE:
                return &quot;DEFENSE&quot;;
            case DECEPTION:
                return &quot;DECEPTION&quot;;
            case MANEUVER:
                return &quot;MANEUVER&quot;;
        }
        return &quot;UNKNOWN&quot;;
    }

    /**
     * Calculates the success grade for a given fighter and action.
     * @param fighter The fighter&#39;s data
     * @param action The combat action
     * @return The success grade
     */
    public int calculateSuccessGrade(int[] fighter, int action) {
      
        int success = 0;

        switch (action) {
            case ATTACK:
                success = fighter[ATTACK];
                break;
            case DEFENSE:
                success = fighter[DEFENSE];
                break;
            case DECEPTION:
                success = fighter[DECEPTION];
                break;
            case MANEUVER:
                success = fighter[MANEUVER];
                break;
        }

        success = Math.max(0, Math.min(100, success));
        return success;

    }

    /**
     * Resolves combat strategies for both the player and the adversary.
     * @param player The player&#39;s data
     * @param playerAction The player&#39;s chosen action
     * @param adversary The adversary&#39;s data
     * @param adversaryAction The adversary&#39;s chosen action
     */
    public void resolveStrategies(int[] player, int playerAction, int[] adversary, int adversaryAction) {
        int playerSuccess = calculateSuccessGrade(player, playerAction);
        int adversarySuccess = calculateSuccessGrade(adversary, adversaryAction);
        

        switch (playerAction) {
            case ATTACK:
                switch (adversaryAction) {
                    case ATTACK:
                        player[VIDA] -= adversarySuccess;
                        adversary[VIDA] -= playerSuccess;
                        break;
                    case DEFENSE:
                        adversary[VIDA] = Math.min(adversary[VIDA] + adversarySuccess, adversary[VIDA_MAX]);
                        player[VIDA] -= adversarySuccess;
                        break;
                    case DECEPTION:
                        adversary[VIDA] -= playerSuccess;
                        break;
                    case MANEUVER:
                        adversary[VIDA] -= playerSuccess;
                        break;
                }
                break;
            case DEFENSE:
                switch (adversaryAction) {
                    case ATTACK:
                        player[VIDA] += Math.min(playerSuccess, player[VIDA_MAX] - player[VIDA]);
                        break;
                    case DEFENSE:
                        player[VIDA] += Math.min(player[VIDA] + playerSuccess, player[VIDA_MAX]);
                        adversary[VIDA] = Math.min(adversary[VIDA] + adversarySuccess, adversary[VIDA_MAX]);
                        break;
                    case DECEPTION:
                        player[VIDA] -= 2 * adversarySuccess;
                        break;
                    case MANEUVER:
                        player[VIDA] -= adversarySuccess;
                        applyPenalty(player);
                        break;
                    // Add other cases as needed
                }
                break;

        }

    }

    /**
     * Applies a penalty to the player&#39;s attack and defense attributes.
     * @param player The player&#39;s data
     */
    public void applyPenalty(int[] player) {
        if (rounds_p &gt; 0) {
            player[ATTACK] = Math.max(player[ATTACK] - 1, 1);
            player[DEFENSE] = Math.max(player[DEFENSE] - 1, 1);
            rounds_p--;

            if (rounds_p == 0) {
                // Restore original values
                player[ATTACK] = player[ATTACK_MAX];
                player[DEFENSE] = player[DEFENSE_MAX];
            }
        }

    }

}
