    private String truncate(String s, int limit) {
        int n = s.length();
        int last = Math.min(n, limit);
     
        if (last &lt; n) {
            //scroll to start index of the last word
            while (last &gt; 0 &amp;&amp; !Character.isWhitespace(s.charAt(last))) last--;
