```
public class Main {
    static StringBuilder valid = new StringBuilder();
    static StringBuilder invalid = new StringBuilder();

    // START Tracking / formatting vars.
    static int permsPerRow = 10;
    static int invalidNumsAdded = 0;
    static int validPerms = 0;
    static int invalidPerms = 0;
    // END Tracking / formatting vars.

    public static void main(String[] args) {
        calculatePerms();
    }

    private static void calculatePerms() {
        char[] chars = {&#39;a&#39;, &#39;b&#39;, &#39;c&#39;, &#39;d&#39;};
        int numsAdded = 0;
        for(int i = 0; i &lt; chars.length; i++) {
            for(int j = 0; j &lt; chars.length; j++) {
                for(int k = 0; k &lt; chars.length; k++) {
                    for(int z = 0; z &lt; chars.length; z++) {
                        // Unsure why, but having the last char in the array be the first mutation bothered me, so
                        // I reversed that.
                        String str = chars[z]+&quot;&quot;+chars[k]+&quot;&quot;+chars[j]+&quot;&quot;+chars[i];
                        // We have the string now, we just need to validate it against the provided rules.
                        if(validString(str)) {
                            // I added the chunk below for formatting, this can be ignored.
                            if(numsAdded &gt;= permsPerRow) {
                                valid.append(&quot;\n&quot;);
                                numsAdded = 0;
                            }
                            // It&#39;s been validated, I append it to the valid StringBuilder.
                            valid.append(str+&quot; &quot;);
                            validPerms++;
                            numsAdded++;
                        }
                    }
                }
            }
        }
        System.out.println(&quot;Valid Perms: &quot;+validPerms);
        System.out.println(valid.toString()+&quot;\n&quot;);
        System.out.println(&quot;Invalid Perms: &quot;+invalidPerms);
        System.out.println(invalid.toString());
    }

    private static boolean validString(String str) {
        // a and d can&#39;t be in the same string
        // b always has to be followed by a
        if(str.toLowerCase().contains(&quot;a&quot;) &amp;&amp; str.toLowerCase().contains(&quot;d&quot;)) {
            logInvalid(str);
            return false;
        }
        char[] strChars = str.toCharArray();

        for(int i = 0; i &lt; strChars.length; i++) {
            if(strChars[i] == &#39;b&#39;) {
                // Check to make sure b isn&#39;t the last char in the sequence. If it is, then &#39;a&#39; can&#39;t follow it,
                // and it&#39;s an invalid permutation.
                if(i+1 &gt; strChars.length-1) {
                    logInvalid(str);
                    return false;
                }
                // If the char following b isn&#39;t a then it&#39;s not a valid perm.
                if(strChars[i+1] != &#39;a&#39;) {
                    logInvalid(str);
                    return false;
                }
            }
        }

        return true;
    }

    // I added an invalid string builder as well, to track the number of invalid combinations, mostly to spot check
    // and ensure the &#39;invalid&#39; permutations were actually breaking the rules.
    private static void logInvalid(String str) {
        if(invalidNumsAdded &gt;= permsPerRow) {
            invalid.append(&quot;\n&quot;);
            invalidNumsAdded = 0;
        }
        invalid.append(str+&quot; &quot;);
        invalidPerms++;
        invalidNumsAdded++;
    }
}
