                digit++;
            }
            if (c &gt;= 33 &amp;&amp; c &lt;= 46 || c == 64) {
                special++;
            }
        }
        if (special &gt;= 1 &amp;&amp; loCount &gt;= 1 &amp;&amp; upCount &gt;= 1 &amp;&amp; digit &gt;= 1) {
            return &quot; &quot;;
        }
        if (loCount == 0) {
            otv += &quot;         &quot; + &quot;\n&quot;;
        }
        if (upCount == 0) {
            otv += &quot;         &quot; + &quot;\n&quot;;
        }
        if (digit == 0) {
            otv += &quot;      &quot; + &quot;\n&quot;;
        }
        if (special == 0) {
            otv += &quot;        &quot; + &quot;\n&quot;;
        }
    }
    else if (password.length() &lt; min) {
        otv += &quot;      &quot; + min + &quot; &quot; + &quot;\n&quot;;
    }
    else if (password.length() &gt; max) {
        otv += &quot;   ,  : &quot; + max + &quot; &quot; + &quot;\n&quot;;
    }
    return otv;
}

