import com.badlogic.gdx.graphics.Texture.TextureFilter;
import com.badlogic.gdx.utils.GdxRuntimeException;
import com.epicoweo.platformereditor.etc.Drawable;
import com.epicoweo.platformereditor.screens.EditorScreen;

public class Tile extends Drawable {

	public String texturePath;
	public boolean isEmpty = false;
	public float rotation = 0;
	public TileTexture tt;
	public TileType type;
	public float width = EditorScreen.gridSquareSize;
	public float height = EditorScreen.gridSquareSize;
	
	public enum TileTexture {
		//maplayout
		EMPTY(&quot;&quot;, 0, &quot;tile&quot;), STONE(&quot;./textures/tiles/stone.png&quot;, 1, &quot;tile&quot;), GRASS(&quot;./textures/tiles/grass.png&quot;, 2, &quot;tile&quot;),
		DIRT(&quot;./textures/tiles/dirt.png&quot;, 3, &quot;tile&quot;), GRAVITYSWAP(&quot;./textures/tiles/gravityswap.png&quot;, 4, &quot;tile&quot;),
		SPEEDBOOST(&quot;./textures/tiles/speedboost_0.png&quot;, 5, &quot;tile&quot;), PLATFORM0(&quot;./textures/tiles/platform_0.png&quot;, 6, &quot;tile&quot;), 
		PLATFORM1(&quot;./textures/tiles/platform_1.png&quot;, 7, &quot;tile&quot;), PLATFORM2(&quot;./textures/tiles/platform_2.png&quot;, 8, &quot;tile&quot;),
		
		//mapetc
		PLAYER(&quot;./textures/player/player_left.png&quot;, 1, &quot;etc&quot;);
		
		String texturePath;
		public Texture texture;
		public int textureId;
		public String type;
		
		TileTexture(String texturePath, int textureId, String type) {
			this.texturePath = texturePath;
			this.textureId = textureId;
			this.type = type;
			try {
				texture = new Texture(this.texturePath);
			} catch(GdxRuntimeException e) {
				texture = new Texture(new Pixmap(16, 16, Format.Alpha));
			}
		}
		
		public static void disposeAll() {
			for(TileTexture t : TileTexture.values()) {
				t.texture.dispose();
			}
		}
	}
	
	public enum TileType {
		
		FULL(&quot;full&quot;), SLOPE_45(&quot;slope45&quot;), PLATFORM(&quot;platform&quot;);
		
		String s;
		TileType(String s) {
			this.s = s;
		}
		
		@Override
		public String toString() {
			return s;
		}
	}
	
	public Tile(float x, float y, float width, float height, TileTexture texture, TileType type) {
		super(x, y, width, height);
		
		texturePath = texture.texturePath;
		
		this.type = type;
		this.tt = texture;
		
		
		if(type == TileType.SLOPE_45) {
			//remove png and add _45.png
			texturePath = texture.texturePath.substring(0, texture.texturePath.length() - 4) + &quot;_45.png&quot;;
		}
		if(texture != TileTexture.EMPTY) {
			if(type != TileType.PLATFORM) {
				Pixmap pixmap16 = new Pixmap(Gdx.files.internal(texturePath));
				Pixmap pixmap64 = new Pixmap(64, 64, pixmap16.getFormat());
				
				pixmap64.setFilter(Filter.NearestNeighbour);
				pixmap64.drawPixmap(pixmap16,
						0, 0, pixmap16.getWidth(), pixmap16.getHeight(),
						0, 0, pixmap64.getWidth(), pixmap64.getHeight()
				);
				
				Texture t64 = new Texture(pixmap64);
				
				if(this.texture != null) {
					this.texture.dispose();
				}
				this.texture = t64;
				
				pixmap16.dispose();
				pixmap64.dispose();
			} else {
				this.height = EditorScreen.gridSquareSize / 4;
				
				Pixmap pixmap16 = new Pixmap(Gdx.files.internal(texturePath));
				Pixmap pixmap64 = new Pixmap(64, 16, pixmap16.getFormat());
				
				pixmap64.setFilter(Filter.NearestNeighbour);
				pixmap64.drawPixmap(pixmap16,
						0, 0, pixmap16.getWidth(), pixmap16.getHeight(),
						0, 0, pixmap64.getWidth(), pixmap64.getHeight()
				);
				
				Texture t64 = new Texture(pixmap64);
				
				if(this.texture != null) {
					this.texture.dispose();
				}
				this.texture = t64;
				
				
				pixmap16.dispose();
				pixmap64.dispose();
			}
		} else {
			this.texture = texture.texture;
			isEmpty = true;
		}
		
	}
	
	public Tile(float x, float y, float width, float height, TileTexture texture) {
		this(x, y, width, height, texture, TileType.FULL);
		
	}
	public Tile(float x, float y, float width, float height) {
		this(x, y, width, height, TileTexture.STONE, TileType.FULL);
		
	}
	
	public Tile duplicate() {
		Tile newTile = new Tile(x, y, width, height, tt, type);
		return newTile;
	}
	
	
}
