```
public class ExampleCompositeKey implements Serializable {

    private String something;
    private String somethingElse;

    @Override
    public boolean equals(Object o) {
        if (this == o) return true;
        if (!(o instanceof ExampleCompositeKey)) return false;
        ExampleCompositeKey that = (ExampleCompositeKey) o;
        return Objects.equals(something, that.something)
                &amp;&amp; Objects.equals(somethingElse, that.somethingElse);
    }

    @Override
    public int hashCode() {
        return Objects.hash(something, somethingElse);
    }
