    &lt;?xml version=&quot;1.0&quot; encoding=&quot;utf-8&quot;?&gt;
    &lt;resources&gt;
    ...
       &lt;item type=&quot;id&quot; name=&quot;someflavorUniqueViewId1&quot;/&gt;
