    public void notificationOpenHandler() {
        try {
            if (getIntent().getExtras() != null) {
                new Handler().postDelayed(() -&gt; {
                    Intent intent = new Intent(ActivitySplash.this, MainActivity.class);
                    intent.setFlags(Intent.FLAG_ACTIVITY_CLEAR_TOP);
                    intent.putExtra(Constant.FCM_TITLE, getIntent().getStringExtra(Constant.FCM_TITLE));
                    intent.putExtra(Constant.FCM_LINK, getIntent().getStringExtra(Constant.FCM_LINK));
                    intent.putExtra(Constant.FCM_BROWSER, getIntent().getIntExtra(Constant.FCM_BROWSER, 0));
                    intent.putExtra(Constant.FCM_IMAGE, getIntent().getStringExtra(Constant.FCM_IMAGE));
                    intent.putExtra(Constant.FCM_EXTRA, getIntent().getStringExtra(Constant.FCM_EXTRA));
                    startActivity(intent);
                    finish();
                    overridePendingTransition(R.anim.fade_in, R.anim.fade_out);
                }, Constant.SPLASH_TIME);
            } else {
                moveToNext();
            }
        } catch (Exception e) {
            moveToNext();
        }
    }

    private void moveToNext() {
        new Handler().postDelayed(() -&gt; {
            Intent intent = new Intent(ActivitySplash.this, MainActivity.class);
            intent.setFlags(Intent.FLAG_ACTIVITY_CLEAR_TOP);
            startActivity(intent);
            finish();
            overridePendingTransition(R.anim.fade_in, R.anim.fade_out);
        }, Constant.SPLASH_TIME);
    }

    public static void setWindowFlag(Activity activity, final int bits, boolean on) {
        Window win = activity.getWindow();
        WindowManager.LayoutParams winParams = win.getAttributes();
        if (on) {
            winParams.flags |= bits;
        } else {
            winParams.flags &amp;= ~bits;
        }
        win.setAttributes(winParams);
    }
}
