    import groovy.json.JsonSlurper
    
    def traverse
    traverse = { tree, keys = [], prefix = &#39;&#39; -&gt;
        switch (tree) {
            case Map:
                tree.each { k, v -&gt;
                    def name = prefix ? &quot;${prefix}.${k}&quot; : k
                    if (!(v instanceof Map)) {
                        keys &lt;&lt; [&quot;$name&quot;: v]
                    } else {
                        keys &lt;&lt; name
                    }
                    traverse(v, keys, name)
                }
                return keys
            case Collection:
                tree.eachWithIndex { e, i -&gt; traverse(e, keys, &quot;${prefix}[$i]&quot;) }
                return keys
            default:
                return keys
        }
    }
    
