		}
	
		public void setAge(int age) {
			this.age = age;
		}
	
		@Override
		public boolean equals(Object obj) {
			if (obj == this)
				return true;
			if (!(obj instanceof Employee))
				return false;
			Employee employee = (Employee) obj;
			return employee.getAge() == this.getAge()
					&amp;&amp; employee.getName() == this.getName();
		}
	
		// commented	
