        String jsonString = &quot;[{\&quot;key1\&quot;: {\&quot;valueA\&quot;: \&quot;\&quot;, \&quot;valueB\&quot;: \&quot;\&quot;}},\n&quot; +
                &quot;{\&quot;key2\&quot;: {\&quot;valueC\&quot;: \&quot;key3\&quot;}},\n&quot; +
