  &lt;constraint name=&quot;__context__&quot; script=&quot;&amp;quot;__context__.getParent() instanceof com.intellij.psi.PsiCodeBlock&amp;quot;&quot; within=&quot;&quot; contains=&quot;&quot; /&gt;
