    abstract class TheDatabase extends RoomDatabase {
        abstract AllDAOs getAllDAOs();
    
        private static TheDatabase instance;
        public static TheDatabase getInstance(Context context) {
            if (instance==null) {
                instance = Room.databaseBuilder(context, TheDatabase.class,&quot;the_database.db&quot;)
                        .allowMainThreadQueries() /* for brevity of the demo */
                        .addCallback(cb) /*&lt;&lt;&lt;&lt;&lt;&lt;&lt;&lt;&lt;&lt; see code below &gt;&gt;&gt;&gt;&gt;&gt;&gt;&gt;&gt;&gt;*/
                        .build();
            }
            return instance;
        }
    
        /* The Call back i.e. the handler */
        static Callback cb = new Callback() {
    
            /* the onOpen method is passed control whenever the database is first accessed, so whenever the App runs */
            @Override
            public void onOpen(@NonNull SupportSQLiteDatabase db) {
                super.onOpen(db);
                Log.d(&quot;DBINFO&quot;,&quot;onOpen Called&quot;);
                /* If not in a transaction then use a transaction */
                boolean transactionStarted = false;
                if (!db.inTransaction()) {
                    Log.d(&quot;DBINFO&quot;,&quot;onOpen - beginning transaction.&quot;);
                    db.beginTransaction();
                    transactionStarted = true;
                }
                /* Check to see if the deletions are to be done */
                /* If so then delete all rows  and importantly set the timestamp to now */
                if (isControlToBeActivated(db)) {
                    Log.d(&quot;DBINFO&quot;,&quot;onOpen - Delete Control Activated&quot;);
                    db.delete(&quot;table1&quot;,null,null);
                    Log.d(&quot;DBINFO&quot;,&quot;onOpen - Deleted all rows from table1.&quot;);
                    /* and more tables if necessary */
                    Log.d(&quot;DBINFO&quot;,&quot;onOpen - beginning transaction.&quot;);
                    db.execSQL(&quot;UPDATE control  SET delete_control = ?;&quot;,new String[]{ String.valueOf (System.currentTimeMillis()/MainActivity.DROP_MILLISECONDS)});
                }
                /* if it was not in a transaction then end the transaction applying the changes */
                if (transactionStarted) {
                    Log.d(&quot;DBINFO&quot;,&quot;onOpen - committing and ending transaction.&quot;);
                    db.setTransactionSuccessful();
                    db.endTransaction();
                    transactionStarted = false;
                }
            }
    
            /* the onCreate method is passed control ONCE in the lifetime of the database */
            /* so add the timestamp here */
            @Override
            public void onCreate(@NonNull SupportSQLiteDatabase db) {
                super.onCreate(db);
                long result;
                Log.d(&quot;DBINFO&quot;,&quot;onCreate Called - Inserting delete_control row&quot;);
                ContentValues cv = new ContentValues();
                cv.put(&quot;delete_control&quot;,System.currentTimeMillis() / MainActivity.DROP_MILLISECONDS);
                result = db.insert(&quot;control&quot;, SQLiteDatabase.CONFLICT_REPLACE,cv);
                Log.d(&quot;DBINFO&quot;,&quot;onCreate Complete - result (rowid of inserted row) is &quot; + String.valueOf(result));
            }
        };
    
        /* Checks to see if the timestamp is a month old or older */
        private static boolean isControlToBeActivated(SupportSQLiteDatabase db) {
            long dateNow = System.currentTimeMillis() / MainActivity.DROP_MILLISECONDS;
            boolean flag = false;
            Cursor csr = db.query(&quot;SELECT (date(delete_control,&#39;unixepoch&#39;,?)) &lt;= date(&#39;now&#39;) FROM control&quot;,new String[]{MainActivity.DATA_DELETE_CONTROL});
            if (csr.moveToFirst()) {
                Log.d(&quot;DBINFO&quot;,&quot;isControlToBeActivated extracted a row. The result being &quot; + csr.getString(0));
                flag = (csr.getInt(0) != 0);
            }
            csr.close();
            return flag;
        }
    }

Finally, to demonstrate some Activity code.

    public class MainActivity extends AppCompatActivity {
    
        public static String DATA_DELETE_CONTROL = &quot;+1 month&quot;;
        public static final long DROP_MILLISECONDS = 1000;
    
        TheDatabase db;
