                        {1, 0, 0},
                        {0, cos(co.x), sin(co.x)},
                        {0, -sin(co.x), cos(co.x)}
                }, new float[][]{
                        {cos(co.y), 0, -sin(co.y)},
                        {0, 1, 0},
                        {sin(co.y), 0, cos(co.y)}
                }),
    
                matmul(new float[][]{
                        {cos(co.z), sin(co.z), 0},
                        {-sin(co.z), cos(co.z), 0},
                        {0, 0, 1}
                }, new float[][]{
                        {p.x - c.x},
                        {p.y - c.y},
                        {p.z - c.z},
                }));
    
        PVector d = new PVector(dxyz[0][0], dxyz[1][0], dxyz[2][0]);
