    public class Book {
    
    	@Id
    	@GeneratedValue
    	private Long id;
    
    	@NaturalId
    	@Column(length = 15)
    	private String isbn;
    
    	@Type(type = &quot;json&quot;)
    	@Column(columnDefinition = &quot;VARCHAR2(1000) CONSTRAINT IS_VALID_JSON CHECK (properties IS JSON)&quot;)
