    	@NaturalId
    	@Column(length = 15)
    	private String isbn;
    
    	@Type(type = &quot;json&quot;)
        @Column(columnDefinition = &quot;NVARCHAR(1000) CHECK(ISJSON(properties) = 1)&quot;)
