public class Node {

    @Id
    @GeneratedValue(strategy = GenerationType.IDENTITY)
    private Long id;

    // other fields, getter/setter, constructors, equals and hashCode methods ...

    @OneToMany(mappedBy = &quot;node&quot;, cascade = CascadeType.ALL)
