    				int xLbl = row &lt; half ? col - row : col - half;
    				int yLbl = row - half;
    				int x = (int) (origin.x + xOff * (col * 2 + 1 - cols));
    				int y = (int) (origin.y + yOff * (row - half) * 3);
    				
