			//deleting folder children
			List&lt;S3ObjectSummary&gt; fileList = s3client.listObjects(bucketName, keyName).getObjectSummaries();
			for (S3ObjectSummary file : fileList) {
				s3client.deleteObject(bucketName, file.getKey());
