    @interface RepeatableAnnotations {
    	RepeatableAnnotation[] value(); 
    }
    
    @Retention(RetentionPolicy.RUNTIME)
    @Target(ElementType.TYPE)
    @Inherited
    @Repeatable(RepeatableAnnotations.class)
