public class App {
    public static void main(String[] args) {
        List&lt;Person&gt; people = Arrays.asList(
                new Person(&quot;New York&quot;, &quot;foo&quot;, &quot;bar&quot;),
                new Person(&quot;New York&quot;, &quot;bar&quot;, &quot;foo&quot;),
