    List&lt;Integer&gt; topFive = new ArrayList&lt;&gt;();

    Integer min = Integer.MIN_VALUE;
    for (int[] row : myArray) {
      for (int value : row) {
        if ((value &gt; min) || topFive.size() &lt; 5) {
          topFive.add(value);
          if (topFive.size() &gt; 5) {
            topFive.remove(min);
          }
          min = topFive.stream().min(Integer::compareTo).orElse(Integer.MIN_VALUE);
        }
