        for ($i = 0; $i &lt; 16; $i++) { $acc += ord($hash[$i]); }
        for ($ci = 0, $hi = 16; $ci &lt; $transformLength; $ci += 1, $hi += 1) {
            if($hi &gt;= strlen($hash)) { $hi = 0; }
            $acc += ord($hash[$hi]);
            $outPass .= $characters[($acc % $chars_num)];
        }
        return $outPass;
    }
    
