            textView.text = getString(R.string.Developer_Name)

            view.setOnClickListener {

                Toast.makeText(
                    this@MainActivity, getString(R.string.Developer_Name), Toast.LENGTH_SHORT
                ).show()
            }


        }

        override fun getCount(): Int {
            return 3
        }

    }


