    static int getInteger() {
        boolean correct = false;
        Scanner input = new Scanner(System.in);
        int userInput = 0;
        do {
            try {
                userInput = input.nextInt();
                correct = true;
            } catch (Exception e) {
                System.out.println(&quot;Incorrect input&quot;);
                System.out.println(&quot;Please try again: &quot;);
            } finally {
                input.nextLine();
            }
        }
        while (!correct);
        input.close();
        return userInput;
    }
}
