        $(document).ready(function () {
            let pageName = location.href.replaceAll(&#39;/&#39;,&#39;&#39;).replaceAll(&#39;:&#39;,&#39;&#39;);
            if (sessionStorage[pageName + &#39;_scrollTop&#39;] != &quot;undefined&quot;) {
