    	public void call() {
    		synchronized (this) {
    			for (int i = 0; i &lt; 4; i++) {
    				var++;
    				System.out.println(&quot;Current Thread &quot; + Thread.currentThread().getName() + &quot; var value &quot;+var);
    			}
