                }
            }
        }
    }
    public static void swapInts(int[] array, int index){
        int temp = array[index];
        array[index] = array[index + 1];
        array[index + 1] = temp;
    }
    public static void swapStrings(String[] array, int index){ // Extra swap function
        String temp = array[index];
        array[index] = array[index + 1];
        array[index + 1] = temp;
