        final var response = webClient.post()
                .uri(&quot;http://some-host:8080/api/theapi&quot;)
                .contentType(MediaType.APPLICATION_OCTET_STREAM)
                .body(BodyInserters
                        .fromDataBuffers(Mono.defer(() -&gt;
                                Mono.just(dataBuffer))
                        )
                )
                .retrieve()
                .bodyToMono(String.class)
