    private static void dequeuePairsMap(Queue&lt;Pair&lt;T, T&gt;&gt; queue, Map&lt;T, Set&lt;Pair&lt;T, T&gt;&gt;&gt; secondToPairMap, Map&lt;T, Set&lt;Pair&lt;T, T&gt;&gt;&gt; firstToPairMap) {

