Here&#39;s an updated solution for MongoDB type No-SQL databases.

```java
package com.eg.config;

import com.mongodb.MongoClientSettings;
import com.mongodb.MongoCredential;
import com.mongodb.ServerAddress;
import com.mongodb.client.MongoClient;
import com.mongodb.client.MongoClients;
import org.springframework.beans.factory.annotation.Qualifier;
import org.springframework.beans.factory.annotation.Value;
import org.springframework.boot.autoconfigure.mongo.MongoProperties;
import org.springframework.boot.context.properties.ConfigurationProperties;
import org.springframework.boot.context.properties.EnableConfigurationProperties;
import org.springframework.context.annotation.Bean;
import org.springframework.context.annotation.Configuration;
import org.springframework.data.mongodb.MongoDatabaseFactory;
import org.springframework.data.mongodb.core.MongoTemplate;
import org.springframework.data.mongodb.core.SimpleMongoClientDatabaseFactory;
import org.springframework.data.mongodb.repository.config.EnableMongoRepositories;

import java.util.ArrayList;
import java.util.List;
import java.util.concurrent.TimeUnit;

@Configuration
@EnableMongoRepositories(basePackages = {&quot;com.example.eg.model&quot;,&quot;com.example.eg.repo&quot;},
        mongoTemplateRef = &quot;egMongoTemplate&quot;)
@EnableConfigurationProperties
public class EgMongoDBConfig {

    @Value(&quot;${connect-timeout}&quot;)
    private Integer connectTimeout;

    @Value(&quot;${local-threshold}&quot;)
    private Integer localThreshold;

    @Value(&quot;${ssl-enabled}&quot;)
    private Boolean sslEnabled;

    @Value(&quot;${ssl-invalid-host-name-allowed}&quot;)
    private Boolean sslInvalidHostNameAllowed;

    @Value(&quot;${max-wait-time}&quot;)
    private Integer maxWaitTime;

    @Value(&quot;${max-connection-idle-time}&quot;)
    private Integer maxConnectionIdleTime;

    @Value(&quot;${max-connection-life-time}&quot;)
    private Integer maxConnectionLifeTime;

    @Value(&quot;${server-selection-timeout}&quot;)
    private Integer serverSelectionTimeout;

    @Value(&quot;${min-heartbeat-frequency}&quot;)
    private Integer minHeartbeatFrequency;

    @Value(&quot;${heartbeat-frequency}&quot;)
    private Integer heartbeatFrequency;

    @Bean(name = &quot;egProperties&quot;)
    @ConfigurationProperties(prefix = &quot;mongo.eg&quot;)
    public MongoProperties egProperties(){
        return new MongoProperties();
    }

    @Bean(name = &quot;egMongoClient&quot;)
    public MongoClient mongoClient(@Qualifier(&quot;egProperties&quot;) MongoProperties mongoProperties){
        List&lt;ServerAddress&gt; serverAddresses = new ArrayList&lt;&gt;();
        String[] addressList = mongoProperties.getHost().split(&quot;,&quot;);
        for (String address: addressList){
            String[] hostAndPort = address.split(&quot;:&quot;);
            String host = hostAndPort[0];
            int port = Integer.parseInt(hostAndPort[1]);
            ServerAddress serverAddress = new ServerAddress(host,port);
            serverAddresses.add(serverAddress);
        }

        MongoCredential credential = MongoCredential.createCredential(mongoProperties.getUsername(),
                        mongoProperties.getAuthenticationDatabase(),
                        mongoProperties.getPassword());

        return MongoClients.create(MongoClientSettings.builder()
                .applyToClusterSettings(builder -&gt; builder
                        .hosts(serverAddresses)
                        .requiredReplicaSetName(mongoProperties.getReplicaSetName())
                        .localThreshold(localThreshold, TimeUnit.MILLISECONDS)
                        .serverSelectionTimeout(serverSelectionTimeout,TimeUnit.MILLISECONDS))
                .applyToSocketSettings(builder -&gt; builder
                        .connectTimeout(connectTimeout,TimeUnit.MILLISECONDS))
                .applyToSslSettings(builder -&gt; builder
                        .enabled(sslEnabled)
                        .invalidHostNameAllowed(sslInvalidHostNameAllowed))
                .applyToConnectionPoolSettings(builder -&gt; builder
                        .maxConnectionIdleTime(maxConnectionIdleTime,TimeUnit.MILLISECONDS)
                        .maxConnectionLifeTime(maxConnectionLifeTime,TimeUnit.MILLISECONDS)
                        .maxWaitTime(maxWaitTime,TimeUnit.MILLISECONDS))
                .applyToServerSettings(builder -&gt; builder
                        .minHeartbeatFrequency(minHeartbeatFrequency,TimeUnit.MILLISECONDS)
                        .heartbeatFrequency(heartbeatFrequency,TimeUnit.MILLISECONDS))
                .credential(credential)
                .build());
    }

    @Bean(name = &quot;egMongoDBFactory&quot;)
    public MongoDatabaseFactory mongoDatabaseFactory(
            @Qualifier(&quot;egMongoClient&quot;) MongoClient mongoClient,
            @Qualifier(&quot;egProperties&quot;) MongoProperties mongoProperties){
        return new SimpleMongoClientDatabaseFactory(mongoClient,mongoProperties.getDatabase());
    }

    @Bean(name = &quot;egMongoTemplate&quot;)
    public MongoTemplate mongoTemplate(@Qualifier(&quot;egMongoDBFactory&quot;) MongoDatabaseFactory mongoDatabaseFactory){
        return new MongoTemplate(mongoDatabaseFactory);
    }

}
```

