            try {
                return callable.call();
            } catch (Error e) {
                throw e;
            } catch (RuntimeException e) {
                throw e; // Also avoids double wrapping CompletionExceptions below.
            } catch (Throwable t) {
                throw new CompletionException(t);
            }
