```java
private Predicate withLabels(Root&lt;Feature&gt; root, CriteriaQuery&lt;?&gt; query, CriteriaBuilder cb, Predicate predicate, List&lt;String&gt; labels) {
    query.distinct(true);

    Join&lt;Feature, Label&gt; joinTag = root.join(&quot;labels&quot;);

    Subquery&lt;Label&gt; labelSubQuery = query.subquery(Label.class);
    Root&lt;Label&gt; label = labelSubQuery.from(Label.class);
    labelSubQuery.select(label.get(&quot;id&quot;)).where(cb.trim(label.get(&quot;name&quot;)).in(labels));

    return cb.and(joinTag.get(&quot;id&quot;).in(labelSubQuery), predicate);
}
