    public static boolean isParenthesisMatch(String str) {
        if (str.charAt(0) == &#39;{&#39;)
            return false;

        Stack&lt;Character&gt; stack = new Stack&lt;Character&gt;();

        char c;
        for(int i=0; i &lt; str.length(); i++) {
            c = str.charAt(i);

            if(c == &#39;(&#39;)
                stack.push(c);
            else if(c == &#39;{&#39;)
                stack.push(c);
            else if(c == &#39;)&#39;)
                if(stack.empty())
                    return false;
                else if(stack.peek() == &#39;(&#39;)
                    stack.pop();
                else
                    return false;
            else if(c == &#39;}&#39;)
                if(stack.empty())
                    return false;
                else if(stack.peek() == &#39;{&#39;)
                    stack.pop();
                else
                    return false;
        }
        return stack.empty();
    }

