            int[][] example = {
                {1, 2, 3, 1, 2},
                {2, 3, 2, 1, 2},
                {3, 2, 1, 2, 2},
                {0, 2, 0, 0, 0},
                {0, 0, 0, 2, 1}};
            
            DestroyIslands destroyIslands = new DestroyIslands(example);
