	public static void selectionSort(int[] list)
	{
		for(int top = list.length - 1; top &gt; 0; top--){
			int largeLoc = 0;
			for(int i = 1; i &lt;= top; i++){
				if(list[i] &gt; list[largeLoc]){
					largeLoc = i;
				}
			}
			int temp = list[top];
			list[top] = list[largeLoc];
			list[largeLoc] = temp;
		}
	}
	public static void bubbleSort(int[]list){
		boolean swap = false;
		do{
			swap = false;
			for(int i=0;i&lt;list.length-1;i++){
				if(list[i] &gt; list[i+1]){
					int temp = list[i];
					list[i] = list[i+1];
					list[i+1] = temp;
					swap = true;
				}
			}
		}while(swap != false);
	}
	public static void insertionSort(int[]list){
		for(int i=0;i&lt;list.length;i++){
			int c = list[i];
			int j = i - 1;
			while(j &gt;= 0 &amp;&amp; list[j] &gt; c){
				list[j + 1] = list[j];
				j--;
				list[j + 1] = c;
			}
		}
	}
	public static void parallel(int[]list){
		Arrays.parallelSort(list);
	}
	public static void quick(int[]list){
		Arrays.sort(list);
	}
	public static int[] shuffle(){
		int[] shuffleArray = new int[100000];
		for(int i=0;i&lt;100000;i++){
			int rand=(int)(Math.random()*100000) + 1;
			shuffleArray[i] = rand;
		}
		return shuffleArray;
	}
	public static int[] descending(){
		int[]descendingArray=new int[100000];
		int c=0;
		for(int i=100000;i&gt;=1;i--){
			descendingArray[c] = i;
			c++;
		}
		return descendingArray;
	}
