        try {
          if (i % length != 0) {
            ArrayList&lt;Integer&gt; tilesNew = (ArrayList&lt;Integer&gt;)tiles.clone();
            Collections.swap(tilesNew, i, i - 1);
            queue.add(tilesNew);
            Main.moves.add(moves + 1);
          }
        }
        catch(Exception e) {}
      }
    }
    return solved;
  }
