        .forPattern(&quot;yyyyMMdd&#39;T&#39;HHmmss&#39;Z&#39;&quot;).withZoneUTC();
    
    public static String formatTimestamp(long timeMilli) {
        return timeFormatter.print(timeMilli);
    }

}

