       &lt;parameter name=&quot;driver.name&quot; value=&quot;chromeDriver&quot; /&gt;           
       &lt;classes&gt;
          &lt;class name=&quot;com.qmetry.qaf.automation.step.client.gherkin.GherkinScenarioFactory&quot; /&gt;
       &lt;/classes&gt;
    &lt;/test&gt;

    &lt;test name=&quot;Test-on-FF&quot;&gt;
