    Mono.just(2)
        .map(i -&gt; i/0) // will produce ArithmeticException
        .doOnError(error -&gt; log.info(&quot;caught error&quot;))
        .subscribe(num -&gt; log.info(&quot;Number: {}&quot;, num ));
}

@Test
public void doOnErrorIfArithmeticException_Mono() {
    Mono.just(2)
